mapper.update:
declare begin update bns_order set ord_due_date = #{list[0].ordDueDate} , ord_note = #{list[0].ordNote} where ord_code = #{list[0].ordCode};
merge into bns_order_detail using dual on(ord_det_code = #{vo.ordDetCode})
when matched then UPDATE SET ord_det_price = #{vo.ordDetPrice}, ord_det_quan = #{vo.ordDetQuan}, ord_det_tot_price = #{vo.ordDetTotPrice} where ord_code = #{vo.ordCode}
WHEN NOT MATCHED THEN insert(ord_det_code,ord_code,prod_code,prod_name,ord_det_price,ord_det_quan,ord_det_tot_price) values('bns_ord_det'||LPAD(get_ord_det_seq,3,'0'),#{vo.ordCode},#{vo.prodCode},#{vo.prodName},#{vo.ordDetPrice},#{vo.ordDetQuan},#{vo.ordDetTotPrice})

mapper.select:
 select emp_num, emp_name
from com_info_emp
  where emp_name =#{keyword}

select acc_code, acc_name
from com_code_account
where acc_name =#{keyword}

select prod_code, prod_name
 from com_code_prod
 where prod_name = #{keyword}
